# Flask example
@app.route('/ai-transaction', methods=['POST'])
def handle_ai_transaction():
    prompt = request.json['prompt']
    credentials = request.json.get('credentials', {})
    result = process_ai_request(prompt, credentials)
    return jsonify(result)

# Webhook handler
@app.route('/webhook/ai-prompt', methods=['POST'])
def ai_prompt_webhook():
    data = request.json
    prompt = data['prompt']
    result = process_ai_request(prompt)
    # Send result back to AI system
    return jsonify(result)
